import React from 'react'
import { Image, Button, Collapse } from 'antd'
import style from './pizzaCard.module.css'
import { CaretRightOutlined } from '@ant-design/icons'
const { Panel } = Collapse;

type PizzaCardTypes = {
    order: boolean
    id: number
    imgPizza: string
    namePizza: string
    descPizza: string
    smallSize: boolean
    mediumSize: boolean
    bigSize: boolean
    price: number
    current: number

    setCurrent: (current: number) => void
    Ordered: (currentPrice: number) => void
}

const PizzaCard: React.FC<PizzaCardTypes> = ({ order, imgPizza, namePizza,
                                               descPizza, smallSize, mediumSize, bigSize, price,
                                               current, setCurrent, Ordered }) => {
    return (
        <div className={style.pizzaCardBlock}>
            <Image
                width={225}
                height={225}
                src={imgPizza}
                fallback="data:image/png;base64,"
            />

            { order
                ? <div>
                    <p className={style.ordered}>Замовлено</p>
                </div>
                : <div>
                    <p className={style.pizzaName}>{ namePizza }</p>
                    <Collapse
                        bordered={false}
                        defaultActiveKey={['1']}
                        expandIcon={({ isActive }) => <CaretRightOutlined rotate={isActive ? 90 : 180} />}
                        expandIconPosition={"right"}
                        className="site-collapse-custom-collapse"
                    >
                        <Panel header="Інгридієнти" key="2" className="site-collapse-custom-panel">
                            <p className={style.pizzaDesc}>{ descPizza }</p>
                        </Panel>
                    </Collapse>

                    <div className={style.pizzaSizePriceBlock}>
                        <div className={style.sizes}>

                            { !mediumSize && setCurrent(15) }

                            <Button type="primary" danger={current !== 15} ghost={current === 15}
                                    disabled={!smallSize && true} shape={"circle"} size={"small"}
                                    onClick={() => setCurrent(15)}
                            >15</Button>

                            <Button type="primary" danger={current !== 30} ghost={current === 30}
                                    disabled={!mediumSize && true} shape={"circle"} size={"small"}
                                    onClick={() => setCurrent(30)}
                            >30</Button>

                            <Button type="primary" danger={current !== 50} ghost={current === 50}
                                    disabled={!bigSize && true} shape={"circle"} size={"small"}
                                    onClick={() => setCurrent(50)}
                            >50</Button>
                        </div>

                        <div className={style.pizzaPrice}>
                            { current === 30
                                ? price
                                : current === 15
                                    ? !mediumSize ? price : price / 2
                                    : current === 50 && price * 2
                            } грн
                        </div>
                    </div>

                    <Button type="primary"
                            style={{ backgroundColor: '#4db6ac', borderColor: '#4db6ac' }}
                            block shape={"round"}
                            onClick={() => {
                                current === 30
                                    ? Ordered(price)
                                    : current === 15
                                    ? !mediumSize ? Ordered(price) : Ordered(price / 2)
                                    : current === 50 && Ordered(price * 2)
                            }}
                    >
                        Замовити
                    </Button>
                </div>
            }
        </div>
    )
};

export default PizzaCard